generated.css:
.song-list {
    width: 375px;
    height: 565px;
    position: absolute;
    left: 0px;
    top: 24px;
    background-color: white;
    transform: translateX(375px);
    transition: transform 0.3s;
    overflow: hidden;
    margin-bottom: 20px;
}
.song-list-header {
    background-color: rgba(104, 99, 99, 0.6);
    padding: 8px 8px 12px 8px;
}
.song-list-header svg {
    width: 20px;
    height: 20px;
    position: relative;
    top: -2px;
}
.song-list-header span {
    padding-left: 8px;
    font-size: 22px;
    font-weight: 500;
    color: white;
}
.song-list-cover {
    padding: 4px 16px 44px 26px;
    display: flex;
    align-items: flex-start;
    font-size: 18px;
    color: white;
    background-color: rgba(104, 99, 99, 0.6);
}
.song-list-cover img {
    border-radius: 4px;
}
.song-list-cover div {
    margin-left: 16px;
    margin-top: 8px;
}
.song-list-info {
    overflow-y: scroll;
    height: 100%;
    width: 400px;
    transform: translateX(-8px);
}
.song-list-info ul{
    width: 375px;
    background-color: white;
    border-top-left-radius: 18px;
    border-top-right-radius: 18px;
    margin: -16px auto 0 auto;
    padding-top: 20px;
    position: relative;
    left: 4px;
}
.song-list-info ul li {
    display: flex;
    justify-content: flex-start;
    align-items: center;
}
.song-list-info ul li:last-child {
    margin-bottom: 18px;
}
.song-list-info ul li .index{
    width: 54px;
    text-align: center;
    color: #616161;
    font-size: 18px;
}
.song-list-info .description {
    display: flex; 
    flex-direction: column;
    justify-content: space-between; 
    height: 89px;
}
.song-list-info .description span:last-child {
    font-size: 10px
}
.song-list-info ul li .song-info {
    padding: 10px 10px 10px 0;
    display: flex;
    flex-direction: column;
    border-bottom: 1px solid #eeeeee;
    flex-grow: 1;
}
.song-list-info ul li .song-info span:last-child {
    font-size: 12px;
    color: #bbbbbb;
}
