section.player { position: absolute; width: 375px; height: 0px; background-color: white;
    bottom: 0px; left: 0px; box-shadow: 0px 0px 999px rgba(0, 0, 0, 0.8); transition: height 0.5s; }
section.player.active { display: block; height: 615px; }

.player-close { position: absolute; top: 4px; left: 4px; }

section.player .player-cover { margin: 310px 0 0 76px; width: 23px; height: 23px; 
    border-radius: 1px; background-size: cover; transition-delay: 0.1s; 
    transition: all 0.4s; transform-origin:left bottom; }
section.player.active .player-cover { transform: scale(10); }
section.player.playing .player-cover { transition: all 0.1s; transform: scale(12);
    margin-left: 54px; box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.4);}
section.player.playing #playButton, section.player #pauseButton { display: none; }
section.player.playing #pauseButton, section.player #playButton { display: block; }

.player-information { margin-top: 50px; }
.player-information > h2, .player-information > div { text-align: center; }

.player-controllers { margin: 44px 32px 0px 40px; display: flex; justify-content: space-around;
    align-items: center; }
